import React from "react";
import { Link } from "react-router-dom";

export default function EventCard({ event }) {
  return (
    <div className="card h-100 border-0 shadow-sm">
      {event.poster && (
        <img src={event.poster} className="card-img-top" alt={event.title} />
      )}
      <div className="card-body d-flex flex-column">
        <div className="d-flex justify-content-between align-items-start mb-2">
          <h5 className="card-title mb-0 flex-grow-1 pe-2 text-truncate">{event.title}</h5>
          {event.featured && <span className="badge bg-primary-subtle text-primary">Featured</span>}
        </div>
        <p className="card-text text-truncate">{event.description}</p>
        <div className="small text-muted mb-2">
          <i className="bi bi-geo-alt me-1" />{event.location}
        </div>
        <div className="mt-auto d-flex justify-content-between align-items-center">
          <small className="text-muted">{new Date(event.start_datetime).toLocaleString()}</small>
          <Link to={`/event/${event.id}`} className="btn btn-outline-primary btn-sm">View</Link>
        </div>
      </div>
    </div>
  );
}
