import React, { useState } from "react";

export default function Hero({ onSearch }) {
  const [q, setQ] = useState("");
  const [location, setLocation] = useState("");

  const submit = (e) => {
    e.preventDefault();
    onSearch?.({ search: q, location });
  };

  return (
    <section className="hero-banner rounded-4 p-4 p-md-5 mb-4">
      <div className="row align-items-center g-3">
        <div className="col-md-7">
          <h1 className="display-5 fw-bold mb-2">Discover, RSVP, and Go</h1>
          <p className="lead mb-0">Find events you love or create your own. Tickets with instant QR codes.</p>
        </div>
        <div className="col-md-5">
          <form className="d-flex gap-2" onSubmit={submit}>
            <input className="form-control form-control-lg" placeholder="Search events" value={q} onChange={(e) => setQ(e.target.value)} />
            <input className="form-control form-control-lg" placeholder="Location" value={location} onChange={(e) => setLocation(e.target.value)} />
            <button className="btn btn-primary btn-lg flex-shrink-0" type="submit">Search</button>
          </form>
        </div>
      </div>
    </section>
  );
}
