import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Hero({ onSearch }) {
  const navigate = useNavigate();
  const slides = [
    {
      url: "https://images.unsplash.com/photo-1500530855697-b586d89ba3ee?q=80&w=1600&auto=format&fit=crop",
      headline: "Discover Amazing Events",
      subtitle: "Find and attend the best events happening around you",
      cta: "Explore Events",
      action: () => navigate("/"),
      gradient: "linear-gradient(135deg, rgba(102, 126, 234, 0.8), rgba(118, 75, 162, 0.8))"
    },
    {
      url: "https://images.unsplash.com/photo-1515165562835-c3b8c20b2e68?q=80&w=1600&auto=format&fit=crop",
      headline: "Create Your Event",
      subtitle: "Organize and share your events with the world",
      cta: "Start Creating",
      action: () => navigate("/organizer/create"),
      gradient: "linear-gradient(135deg, rgba(240, 147, 251, 0.8), rgba(245, 87, 108, 0.8))"
    },
    {
      url: "https://images.unsplash.com/photo-1492684223066-81342ee5ff30?q=80&w=1600&auto=format&fit=crop",
      headline: "Connect & Network",
      subtitle: "Meet like-minded people and build lasting connections",
      cta: "Join Now",
      action: () => navigate("/register"),
      gradient: "linear-gradient(135deg, rgba(79, 172, 254, 0.8), rgba(0, 242, 254, 0.8))"
    },
  ];

  const categories = [
    { icon: "ðŸŽƒ", label: "Halloween", color: "#ff6b6b" },
    { icon: "ðŸŽµ", label: "Music", color: "#4ecdc4" },
    { icon: "ðŸŒƒ", label: "Nightlife", color: "#45b7d1" },
    { icon: "ðŸŽ­", label: "Performing & Visual", color: "#f9ca24" },
    { icon: "ðŸ’•", label: "Dating", color: "#f0932b" },
    { icon: "ðŸŽ®", label: "Hobbies", color: "#eb4d4b" },
    { icon: "ðŸ’¼", label: "Business", color: "#6c5ce7" },
    { icon: "ðŸ•", label: "Food & Drink", color: "#a29bfe" },
  ];

  const [idx, setIdx] = useState(0);
  const next = () => setIdx((i) => (i + 1) % slides.length);
  const prev = () => setIdx((i) => (i - 1 + slides.length) % slides.length);

  useEffect(() => {
    const id = setInterval(next, 6000);
    return () => clearInterval(id);
  }, []);

  const onCategoryClick = (label) => {
    if (onSearch) onSearch({ search: label });
    window.scrollTo({ top: document.body.scrollHeight / 3, behavior: "smooth" });
  };

  return (
    <>
      <section className="hero-carousel position-relative">
        <img src={slides[idx].url} alt="hero" className="w-100 hero-slide" />
        <div 
          className="hero-overlay"
          style={{ background: slides[idx].gradient }}
        >
          <div className="container">
            <div className="row justify-content-center text-center">
              <div className="col-lg-8">
                <h1 className="display-3 fw-bold mb-4">{slides[idx].headline}</h1>
                <p className="lead mb-4 fs-4">{slides[idx].subtitle}</p>
                <button className="btn btn-light btn-lg px-5 py-3" onClick={slides[idx].action}>
                  {slides[idx].cta}
                </button>
              </div>
            </div>
          </div>
        </div>
        <button className="hero-nav hero-prev" onClick={prev} aria-label="Previous">â€¹</button>
        <button className="hero-nav hero-next" onClick={next} aria-label="Next">â€º</button>

        {/* Slide Indicators */}
        <div className="position-absolute bottom-0 start-50 translate-middle-x mb-4">
          <div className="d-flex gap-2">
            {slides.map((_, i) => (
              <button
                key={i}
                className={`btn rounded-circle p-0 ${i === idx ? 'bg-white' : 'bg-white bg-opacity-50'}`}
                style={{ width: '12px', height: '12px' }}
                onClick={() => setIdx(i)}
                aria-label={`Go to slide ${i + 1}`}
              />
            ))}
          </div>
        </div>
      </section>

      {/* Enhanced Categories */}
      <div className="container-fluid px-4">
        <div className="categories-scroll d-flex gap-4 justify-content-center">
          {categories.map((category) => (
            <button key={category.label} className="text-center category-item btn p-0 border-0 bg-transparent" onClick={() => onCategoryClick(category.label)}>
              <div 
                className="category-circle mx-auto"
                style={{ 
                  background: `linear-gradient(135deg, ${category.color}20, ${category.color}10)`,
                  borderColor: category.color + '30'
                }}
              >
                <span style={{ fontSize: '2.5rem' }}>{category.icon}</span>
              </div>
              <div className="category-label">{category.label}</div>
            </button>
          ))}
        </div>
      </div>
    </>
  );
}
