import React, { useState } from "react";
import { Link } from "react-router-dom";

export default function EventCard({ event }) {
  const [isHovered, setIsHovered] = useState(false);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    if (diffDays === 0) return "Today";
    if (diffDays === 1) return "Tomorrow";
    if (diffDays < 7) return `${diffDays} days`;
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: date.getFullYear() !== now.getFullYear() ? 'numeric' : undefined });
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit', hour12: true });
  };

  const getAvailabilityColor = () => {
    const ratio = event.seats_left / event.capacity;
    if (ratio > 0.5) return '#10b981'; // green
    if (ratio > 0.2) return '#f59e0b'; // yellow
    return '#ef4444'; // red
  };

  return (
    <article
      className="event-card-enhanced"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="event-card-inner">
        {/* Image Section */}
        <div className="event-image-container">
          {event.poster ? (
            <img
              src={event.poster}
              alt={event.title}
              className="event-image"
            />
          ) : (
            <div className="event-image-placeholder">
              <div className="placeholder-icon">ðŸŽ«</div>
              <div className="placeholder-pattern"></div>
            </div>
          )}

          {/* Badges */}
          <div className="event-badges">
            {event.featured && (
              <div className="badge-featured">
                <i className="bi bi-star-fill me-1"></i>
                Featured
              </div>
            )}
            <div className="badge-category">
              <i className="bi bi-tag-fill me-1"></i>
              Event
            </div>
          </div>

          {/* Hover Overlay */}
          <div className={`event-overlay ${isHovered ? 'active' : ''}`}>
            <div className="overlay-content">
              <Link to={`/event/${event.id}`} className="btn-view-event">
                <i className="bi bi-eye-fill me-2"></i>
                View Details
              </Link>
            </div>
          </div>
        </div>

        {/* Content Section */}
        <div className="event-content">
          <div className="event-header">
            <h3 className="event-title">{event.title}</h3>
            <div className="event-rating">
              <div className="rating-stars">
                {[...Array(5)].map((_, i) => (
                  <i key={i} className={`bi ${i < 4 ? 'bi-star-fill' : 'bi-star'}`}></i>
                ))}
              </div>
              <span className="rating-text">4.2</span>
            </div>
          </div>

          <p className="event-description">{event.description}</p>

          <div className="event-meta">
            <div className="meta-item">
              <i className="bi bi-geo-alt-fill"></i>
              <span>{event.location}</span>
            </div>
            <div className="meta-item">
              <i className="bi bi-calendar-event-fill"></i>
              <span>{formatDate(event.start_datetime)}</span>
            </div>
            <div className="meta-item">
              <i className="bi bi-clock-fill"></i>
              <span>{formatTime(event.start_datetime)}</span>
            </div>
          </div>

          <div className="event-footer">
            <div className="availability-info">
              <div className="availability-bar">
                <div
                  className="availability-fill"
                  style={{
                    width: `${(event.seats_left / event.capacity) * 100}%`,
                    backgroundColor: getAvailabilityColor()
                  }}
                ></div>
              </div>
              <div className="availability-text">
                <span className="seats-left">{event.seats_left}</span>
                <span className="seats-total">/{event.capacity}</span>
                <span className="seats-label">seats left</span>
              </div>
            </div>

            <div className="event-actions">
              <button className="btn-favorite">
                <i className="bi bi-heart"></i>
              </button>
              <Link to={`/event/${event.id}`} className="btn-primary-custom">
                Book Now
              </Link>
            </div>
          </div>
        </div>
      </div>
    </article>
  );
}
