import React, { useState, useRef, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { createEvent } from "../actions/eventActions";

export default function CreateEventScreen() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading, error, success } = useSelector((s) => s.eventCreate || {});

  const [form, setForm] = useState({ title: "", description: "", start_datetime: "", location: "", capacity: 0, featured: false });
  const [poster, setPoster] = useState(null);
  const [images, setImages] = useState([]);
  const posterRef = useRef(null);
  const imagesRef = useRef(null);

  const change = (e) => setForm({ ...form, [e.target.name]: e.target.type === "checkbox" ? e.target.checked : e.target.value });

  const resetAll = () => {
    setForm({ title: "", description: "", start_datetime: "", location: "", capacity: 0, featured: false });
    setPoster(null);
    setImages([]);
    if (posterRef.current) posterRef.current.value = "";
    if (imagesRef.current) imagesRef.current.value = "";
  };

  useEffect(() => {
    if (success) {
      resetAll();
      navigate("/organizer/events");
    }
  }, [success, navigate]);

  const submit = (e) => {
    e.preventDefault();
    const fd = new FormData();
    Object.entries(form).forEach(([k, v]) => fd.append(k, v));
    if (poster) fd.append("poster", poster);
    if (images && images.length) {
      Array.from(images).forEach((file) => fd.append("images", file));
    }
    dispatch(createEvent(fd));
  };

  return (
    <div className="container-fluid px-4">
      <div className="row justify-content-center">
        <div className="col-lg-9">
          <div className="card border-0 shadow-sm" style={{ borderRadius: 12 }}>
            <div className="card-body p-4">
              <div className="d-flex justify-content-between align-items-center mb-4">
                <h3 className="mb-0">Create Event</h3>
                <div>
                  <button className="btn btn-outline-secondary me-2" onClick={() => navigate(-1)}>Back</button>
                  <small className="text-muted">Organizers only</small>
                </div>
              </div>

              {error && <div className="alert alert-danger">{String(error)}</div>}
              {success && <div className="alert alert-success">Event created — redirecting…</div>}

              <div className="row g-4">
                <div className="col-lg-7">
                  <form onSubmit={submit} onReset={resetAll}>
                    <div className="mb-3">
                      <label className="form-label">Title</label>
                      <input name="title" className="form-control form-control-lg" value={form.title} onChange={change} required />
                    </div>

                    <div className="mb-3">
                      <label className="form-label">Description</label>
                      <textarea name="description" className="form-control" rows="5" value={form.description} onChange={change} required />
                    </div>

                    <div className="row g-3 mb-3">
                      <div className="col-md-6">
                        <label className="form-label">Start Date & Time</label>
                        <input name="start_datetime" type="datetime-local" className="form-control" value={form.start_datetime} onChange={change} required />
                      </div>
                      <div className="col-md-6">
                        <label className="form-label">Location</label>
                        <input name="location" className="form-control" value={form.location} onChange={change} required />
                      </div>
                    </div>

                    <div className="row g-3 align-items-end mb-3">
                      <div className="col-md-4">
                        <label className="form-label">Capacity</label>
                        <input name="capacity" type="number" min="0" className="form-control" value={form.capacity} onChange={change} />
                      </div>
                      <div className="col-md-4">
                        <label className="form-label">Poster</label>
                        <input ref={posterRef} type="file" accept="image/*" className="form-control" onChange={(e) => setPoster(e.target.files[0])} />
                      </div>
                      <div className="col-md-4 d-flex justify-content-start">
                        <div className="form-check mb-2">
                          <input id="featured" name="featured" type="checkbox" className="form-check-input" checked={form.featured} onChange={change} />
                          <label htmlFor="featured" className="form-check-label ms-2">Featured</label>
                        </div>
                      </div>
                    </div>

                    <div className="mb-3">
                      <label className="form-label">Gallery Images</label>
                      <input ref={imagesRef} type="file" accept="image/*" multiple className="form-control" onChange={(e) => setImages(e.target.files)} />
                      {images && images.length > 0 && (
                        <div className="mt-3 d-flex flex-wrap gap-2">
                          {Array.from(images).map((file, idx) => (
                            <div key={idx} className="border rounded overflow-hidden" style={{ width: 72, height: 72 }}>
                              <img src={URL.createObjectURL(file)} alt={file.name} style={{ width: "100%", height: "100%", objectFit: "cover" }} />
                            </div>
                          ))}
                        </div>
                      )}
                    </div>

                    <div className="d-flex gap-2 mt-4">
                      <button className="btn btn-primary" type="submit" disabled={loading}>Create Event</button>
                      <button className="btn btn-outline-secondary" type="reset">Reset</button>
                    </div>
                  </form>
                </div>

                <aside className="col-lg-5">
                  <div className="card border-0" style={{ background: 'linear-gradient(180deg,#ffffff,#f8fafc)', borderRadius: 10 }}>
                    <div className="card-body">
                      <h5 className="mb-3">Preview</h5>
                      <div style={{ borderRadius: 8, overflow: 'hidden', background: '#fff', border: '1px solid rgba(15,23,42,0.04)' }}>
                        {poster ? (
                          <img src={URL.createObjectURL(poster)} alt="poster" style={{ width: '100%', height: 200, objectFit: 'cover' }} />
                        ) : (
                          <div style={{ height: 200, display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#94a3b8' }}>Poster preview</div>
                        )}
                        <div className="p-3">
                          <div className="d-flex justify-content-between align-items-start mb-2">
                            <h6 className="mb-0" style={{ fontWeight: 700 }}>{form.title || "Event title"}</h6>
                            {form.featured && <span className="badge bg-warning text-dark">Featured</span>}
                          </div>
                          <div className="small text-muted mb-2">{form.location || "Location"}</div>
                          <p className="small text-muted" style={{ minHeight: 48 }}>{form.description || "Short description will appear here."}</p>
                          <div className="d-flex justify-content-between align-items-center mt-3">
                            <div>
                              <div className="fw-semibold">{form.start_datetime ? new Date(form.start_datetime).toLocaleString() : "Date & time"}</div>
                              <div className="small text-muted">{form.capacity ? `${form.capacity} seats` : "Capacity"}</div>
                            </div>
                            <div>
                              <button className="btn btn-sm btn-outline-primary" onClick={() => navigate('/')}>Publish later</button>
                            </div>
                          </div>
                        </div>
                      </div>

                      <div className="mt-3">
                        <h6 className="small text-muted mb-2">Gallery</h6>
                        <div className="d-flex flex-wrap gap-2">
                          {images && images.length > 0 ? (
                            Array.from(images).slice(0, 6).map((file, idx) => (
                              <div key={idx} style={{ width: 64, height: 64, overflow: 'hidden', borderRadius: 8, border: '1px solid rgba(15,23,42,0.04)' }}>
                                <img src={URL.createObjectURL(file)} alt={file.name} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
                              </div>
                            ))
                          ) : (
                            <div className="text-muted small">No gallery images yet</div>
                          )}
                        </div>
                      </div>
                    </div>
                  </div>
                </aside>
              </div>

            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
