import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams, useNavigate } from "react-router-dom";
import { getEventDetails, rsvpEvent, likeEvent } from "../actions/eventActions";
import Loader from "../components/Loader";

export default function EventDetailScreen() {
  const { id } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { loading, error, event } = useSelector((s) => s.eventDetails);
  const rsvp = useSelector((s) => s.eventRsvp);
  const userInfo = useSelector((state) => state.userLogin.userInfo);
  const [isLiked, setIsLiked] = useState(false);

  useEffect(() => { dispatch(getEventDetails(id)); }, [dispatch, id]);

  const onRsvp = () => dispatch(rsvpEvent(id));

  const handleLike = async () => {
    if (!userInfo) return;
    try {
      const result = await dispatch(likeEvent(event.id));
      setIsLiked(result.liked);
    } catch (error) {
      console.error('Like error:', error);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    });
  };

  if (loading) return (
    <div className="enhanced-loader">
      <div className="loader-container">
        <div className="loader-spinner spinner-large">
          <div className="spinner-ring"></div>
          <div className="spinner-ring"></div>
          <div className="spinner-ring"></div>
        </div>
        <div className="loader-text">Loading event details...</div>
      </div>
    </div>
  );

  if (error) return (
    <div className="event-detail-screen animate-fade-in">
      <div className="container py-5">
        <div className="alert alert-danger text-center animate-scale-in" style={{
          borderRadius: 'var(--border-radius)',
          border: 'none',
          boxShadow: 'var(--card-shadow)',
          background: 'rgba(239, 68, 68, 0.1)',
          backdropFilter: 'blur(10px)'
        }}>
          <i className="bi bi-exclamation-triangle-fill me-2" style={{ fontSize: '2rem' }}></i>
          <div className="mt-2">
            <h5 className="mb-2">Oops! Something went wrong</h5>
            <p className="mb-0">{String(error)}</p>
          </div>
        </div>
      </div>
    </div>
  );

  if (!event?.id) return null;

  return (
    <div className="event-detail-screen animate-fade-in">
      <div className="container-fluid px-4">
        {/* Back Button */}
        <div className="mb-4">
          <button
            className="btn btn-outline-secondary back-btn animate-slide-in-left"
            onClick={() => navigate(-1)}
            style={{
              borderRadius: 'var(--border-radius-sm)',
              border: '2px solid rgba(0, 0, 0, 0.1)',
              transition: 'all 0.3s ease',
              fontWeight: '600'
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateX(-4px)';
              e.target.style.boxShadow = '0 8px 25px rgba(0, 0, 0, 0.15)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'translateX(0)';
              e.target.style.boxShadow = 'none';
            }}
          >
            <i className="bi bi-arrow-left me-2"></i>
            Back to Events
          </button>
        </div>

        <div className="row g-4">
          {/* Main Content */}
          <div className="col-lg-8">
            <div className="event-detail-card animate-scale-in">
              {/* Poster Image */}
              <div className="event-poster-container">
                {event.poster ? (
                  <div style={{
                    position: 'relative',
                    width: '100%',
                    height: '100%',
                    overflow: 'hidden'
                  }}>
                    <img
                      src={event.poster}
                      alt={event.title}
                      className="event-poster"
                      style={{
                        width: '100%',
                        height: '100%',
                        objectFit: 'cover',
                        transition: 'transform 0.5s ease'
                      }}
                    />
                    <div style={{
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      right: 0,
                      bottom: 0,
                      background: 'linear-gradient(135deg, rgba(0,0,0,0.1) 0%, rgba(0,0,0,0.3) 100%)',
                      zIndex: 1
                    }}></div>
                  </div>
                ) : (
                  <div className="event-poster-placeholder">
                    <div className="placeholder-icon" style={{ fontSize: '4rem', opacity: 0.7 }}>ðŸŽ«</div>
                    <div className="placeholder-text" style={{ fontSize: '1.2rem', fontWeight: '600', color: '#64748b' }}>Event Image</div>
                  </div>
                )}

                {/* Badges */}
                <div className="event-badges">
                  {event.featured && (
                    <div className="badge-featured animate-bounce-in">
                      <i className="bi bi-star-fill me-1"></i>
                      Featured
                    </div>
                  )}
                  {event.category && (
                    <div className="badge-category animate-fade-in">
                      <i className="bi bi-tag-fill me-1"></i>
                      {event.category.name}
                    </div>
                  )}
                </div>

                {/* Like Button */}
                {userInfo && (
                  <button
                    className={`btn-favorite ${isLiked ? 'liked' : ''} animate-scale-in`}
                    onClick={handleLike}
                    title={isLiked ? 'Remove from watchlist' : 'Add to watchlist'}
                    style={{
                      transition: 'all 0.3s ease',
                      transform: isLiked ? 'scale(1.1)' : 'scale(1)'
                    }}
                  >
                    <i className={`bi ${isLiked ? 'bi-heart-fill' : 'bi-heart'}`}></i>
                  </button>
                )}
              </div>

              {/* Event Content */}
              <div className="event-content">
                <div className="event-header animate-fade-in-up">
                  <h1 className="event-title" style={{
                    fontSize: 'clamp(2rem, 4vw, 3rem)',
                    fontWeight: '900',
                    background: 'var(--primary-gradient)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    backgroundClip: 'text',
                    marginBottom: '1rem',
                    lineHeight: '1.2'
                  }}>{event.title}</h1>
                  <div className="event-rating">
                    <div className="rating-stars">
                      {[...Array(5)].map((_, i) => (
                        <i key={i} className={`bi ${i < 4 ? 'bi-star-fill' : 'bi-star'}`} style={{ color: '#fbbf24', fontSize: '1.1rem' }}></i>
                      ))}
                    </div>
                    <span className="rating-text" style={{ color: '#64748b', fontWeight: '600' }}>4.2 (128 reviews)</span>
                  </div>
                </div>

                <div className="event-meta animate-fade-in-up" style={{ animationDelay: '0.2s' }}>
                  <div className="meta-item">
                    <i className="bi bi-calendar-event-fill" style={{ color: '#3b82f6' }}></i>
                    <span style={{ fontWeight: '600', color: '#1e293b' }}>{formatDate(event.start_datetime)}</span>
                  </div>
                  <div className="meta-item">
                    <i className="bi bi-clock-fill" style={{ color: '#10b981' }}></i>
                    <span style={{ fontWeight: '600', color: '#1e293b' }}>{formatTime(event.start_datetime)}</span>
                  </div>
                  <div className="meta-item">
                    <i className="bi bi-geo-alt-fill" style={{ color: '#f59e0b' }}></i>
                    <span style={{ fontWeight: '600', color: '#1e293b' }}>{event.location}</span>
                  </div>
                </div>

                <div className="event-description animate-fade-in-up" style={{ animationDelay: '0.4s' }}>
                  <h3 style={{
                    fontSize: '1.5rem',
                    fontWeight: '800',
                    color: '#1e293b',
                    marginBottom: '1rem',
                    borderBottom: '3px solid var(--primary-gradient)',
                    paddingBottom: '0.5rem',
                    display: 'inline-block'
                  }}>About This Event</h3>
                  <p style={{
                    fontSize: '1.1rem',
                    lineHeight: '1.7',
                    color: '#64748b',
                    margin: 0
                  }}>{event.description}</p>
                </div>

                {/* Organizer Info */}
                {event.organizer && (
                  <div className="event-organizer animate-fade-in-up" style={{ animationDelay: '0.6s' }}>
                    <h4 style={{
                      fontSize: '1.25rem',
                      fontWeight: '700',
                      color: '#1e293b',
                      marginBottom: '1.5rem'
                    }}>Organized by</h4>
                    <div className="organizer-info" style={{
                      padding: '1.5rem',
                      background: 'rgba(59, 130, 246, 0.05)',
                      borderRadius: 'var(--border-radius)',
                      border: '1px solid rgba(59, 130, 246, 0.1)'
                    }}>
                      <div className="organizer-avatar">
                        {event.organizer.avatar ? (
                          <img src={event.organizer.avatar} alt={event.organizer.username} />
                        ) : (
                          <div className="avatar-placeholder" style={{
                            background: 'var(--primary-gradient)',
                            color: 'white',
                            fontWeight: '700',
                            fontSize: '1.5rem'
                          }}>
                            {event.organizer.username?.charAt(0).toUpperCase()}
                          </div>
                        )}
                      </div>
                      <div className="organizer-details">
                        <div className="organizer-name" style={{ fontWeight: '700', color: '#1e293b' }}>{event.organizer.username}</div>
                        <div className="organizer-stats" style={{ color: '#64748b', fontSize: '0.9rem' }}>Event Organizer</div>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="col-lg-4">
            <div className="event-sidebar animate-slide-in-right">
              {/* RSVP Card */}
              <div className="rsvp-card animate-scale-in">
                <div className="availability-section">
                  <div className="availability-header">
                    <span className="availability-label" style={{ fontWeight: '700', color: '#1e293b' }}>Tickets Available</span>
                    <span className="availability-count" style={{
                      background: 'var(--primary-gradient)',
                      color: 'white',
                      padding: '0.5rem 1rem',
                      borderRadius: '20px',
                      fontWeight: '700',
                      fontSize: '0.9rem'
                    }}>{event.seats_left} / {event.capacity}</span>
                  </div>
                  <div className="availability-bar">
                    <div
                      className="availability-fill"
                      style={{
                        width: `${(event.seats_left / event.capacity) * 100}%`,
                        background: event.seats_left > event.capacity * 0.5 ? 'var(--success-gradient)' :
                                   event.seats_left > event.capacity * 0.2 ? 'var(--warning-gradient)' : 'var(--dark-gradient)',
                        transition: 'width 0.3s ease'
                      }}
                    ></div>
                  </div>
                  <div className="availability-text" style={{ fontWeight: '600', color: '#64748b' }}>
                    {event.seats_left > 0 ? `${event.seats_left} seats left` : 'Sold out'}
                  </div>
                </div>

                <button
                  className="btn btn-primary btn-rsvp w-100 btn-animated"
                  onClick={onRsvp}
                  disabled={event.seats_left === 0}
                  style={{
                    background: event.seats_left === 0 ? '#94a3b8' : 'var(--primary-gradient)',
                    border: 'none',
                    padding: '1.2rem 2rem',
                    borderRadius: 'var(--border-radius)',
                    fontWeight: '700',
                    fontSize: '1.1rem',
                    boxShadow: event.seats_left === 0 ? 'none' : '0 8px 25px rgba(59, 130, 246, 0.3)',
                    transition: 'all 0.3s ease'
                  }}
                >
                  {event.seats_left === 0 ? (
                    <>
                      <i className="bi bi-x-circle me-2"></i>
                      Sold Out
                    </>
                  ) : (
                    <>
                      <i className="bi bi-ticket-perforated me-2"></i>
                      RSVP Now
                    </>
                  )}
                </button>

                {rsvp.ticket && (
                  <div className="ticket-info animate-bounce-in">
                    <div className="ticket-header">
                      <i className="bi bi-check-circle-fill text-success me-2" style={{ fontSize: '1.2rem' }}></i>
                      <span className="fw-semibold" style={{ color: '#1e293b', fontSize: '1.1rem' }}>You're Going!</span>
                    </div>
                    <div className="ticket-details">
                      <div className="ticket-id">
                        <span className="label" style={{ color: '#64748b', fontSize: '0.9rem' }}>Ticket ID:</span>
                        <span className="value" style={{ fontWeight: '700', color: '#1e293b', fontSize: '1rem' }}>{rsvp.ticket.id}</span>
                      </div>
                      {rsvp.ticket.qr_image && (
                        <div className="qr-code">
                          <img
                            src={rsvp.ticket.qr_image}
                            alt="Ticket QR Code"
                            className="img-fluid"
                            style={{
                              borderRadius: 'var(--border-radius-sm)',
                              boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)'
                            }}
                          />
                        </div>
                      )}
                    </div>
                  </div>
                )}
              </div>

              {/* Share Section */}
              <div className="share-card animate-fade-in-up" style={{ animationDelay: '0.3s' }}>
                <h5 className="share-title" style={{ fontWeight: '700', color: '#1e293b' }}>Share This Event</h5>
                <div className="share-buttons">
                  <button className="btn btn-outline-primary share-btn btn-animated" style={{ borderRadius: 'var(--border-radius-sm)' }}>
                    <i className="bi bi-facebook me-2"></i>
                    Facebook
                  </button>
                  <button className="btn btn-outline-info share-btn btn-animated" style={{ borderRadius: 'var(--border-radius-sm)' }}>
                    <i className="bi bi-twitter me-2"></i>
                    Twitter
                  </button>
                  <button className="btn btn-outline-success share-btn btn-animated" style={{ borderRadius: 'var(--border-radius-sm)' }}>
                    <i className="bi bi-whatsapp me-2"></i>
                    WhatsApp
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
