import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProfile, updateProfile } from "../actions/userActions";

export default function ProfileScreen() {
  const dispatch = useDispatch();
  const { loading, error, profile, updated } = useSelector((s) => s.userProfile);
  const [form, setForm] = useState({ first_name: "", last_name: "" });
  const [avatar, setAvatar] = useState(null);

  useEffect(() => { dispatch(getProfile()); }, [dispatch]);
  useEffect(() => { if (profile) setForm({ first_name: profile.first_name || "", last_name: profile.last_name || "" }); }, [profile]);

  const submit = (e) => {
    e.preventDefault();
    const fd = new FormData();
    fd.append("first_name", form.first_name);
    fd.append("last_name", form.last_name);
    if (avatar) fd.append("avatar", avatar);
    dispatch(updateProfile(fd));
  };

  return (
    <div className="row justify-content-center">
      <div className="col-md-6">
        <h3>Profile</h3>
        {error && <div className="alert alert-danger">{String(error)}</div>}
        {updated && <div className="alert alert-success">Profile updated</div>}
        <form onSubmit={submit}>
          <div className="mb-3"><label className="form-label">First Name</label><input className="form-control" value={form.first_name} onChange={(e) => setForm({ ...form, first_name: e.target.value })} /></div>
          <div className="mb-3"><label className="form-label">Last Name</label><input className="form-control" value={form.last_name} onChange={(e) => setForm({ ...form, last_name: e.target.value })} /></div>
          <div className="mb-3"><label className="form-label">Avatar</label><input type="file" className="form-control" onChange={(e) => setAvatar(e.target.files[0])} /></div>
          <button className="btn btn-primary" type="submit" disabled={loading}>Save</button>
        </form>
      </div>
    </div>
  );
}
