import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getEventDetails, rsvpEvent } from "../actions/eventActions";
import Loader from "../components/Loader";

export default function EventDetailScreen() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { loading, error, event } = useSelector((s) => s.eventDetails);
  const rsvp = useSelector((s) => s.eventRsvp);

  useEffect(() => { dispatch(getEventDetails(id)); }, [dispatch, id]);

  const onRsvp = () => dispatch(rsvpEvent(id));

  if (loading) return <Loader />;
  if (error) return <div className="alert alert-danger">{String(error)}</div>;
  if (!event?.id) return null;
  return (
    <div className="row g-4">
      <div className="col-md-6">
        {event.poster && <img src={event.poster} alt={event.title} className="img-fluid rounded" />}
      </div>
      <div className="col-md-6">
        <h3>{event.title}</h3>
        <p className="text-muted">{new Date(event.start_datetime).toLocaleString()} Â· {event.location}</p>
        <p>{event.description}</p>
        <p><strong>Seats left:</strong> {event.seats_left} / {event.capacity}</p>
        <button className="btn btn-primary" onClick={onRsvp}>RSVP</button>
        {rsvp.ticket && (
          <div className="mt-3">
            <p className="mb-1">Ticket ID: {rsvp.ticket.id}</p>
            {rsvp.ticket.qr_image && <img alt="Ticket QR" src={rsvp.ticket.qr_image} className="img-thumbnail" />}
          </div>
        )}
      </div>
    </div>
  );
}
