import React from "react";
import { Link } from "react-router-dom";

export default function EventCard({ event }) {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    if (diffDays === 0) return "Today";
    if (diffDays === 1) return "Tomorrow";
    if (diffDays < 7) return `${diffDays} days`;
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: date.getFullYear() !== now.getFullYear() ? 'numeric' : undefined });
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit', hour12: true });
  };

  return (
    <article className="card event-card border-0 shadow-sm h-100" style={{ borderRadius: 12, overflow: 'hidden' }}>
      {event.poster ? (
        <div className="position-relative">
          <img src={event.poster} className="card-img-top" alt={event.title} style={{ height: 180, objectFit: 'cover' }} />
          {event.featured && (
            <div className="position-absolute top-0 end-0 m-3">
              <span className="badge" style={{ background: 'linear-gradient(135deg,#f093fb,#f5576c)', color: 'white', padding: '0.4rem .7rem', borderRadius: 16, fontWeight: 700, fontSize: '.75rem' }}>Featured</span>
            </div>
          )}
        </div>
      ) : (
        <div style={{ height: 140, background: 'linear-gradient(180deg,#f8fafc,#eef2ff)', display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#64748b' }}>
          <div style={{ fontSize: '2.2rem' }}>ðŸŽ«</div>
        </div>
      )}

      <div className="card-body d-flex flex-column">
        <h5 className="card-title mb-1" style={{ fontWeight: 700 }}>{event.title}</h5>
        <p className="card-text text-muted small mb-2" style={{ minHeight: 42, overflow: 'hidden' }}>{event.description}</p>
        <div className="mb-3 small text-muted d-flex gap-2 align-items-center">
          <i className="bi bi-geo-alt-fill" style={{ color: '#3b82f6' }} />
          <span>{event.location}</span>
        </div>

        <div className="mt-auto d-flex justify-content-between align-items-center">
          <div>
            <div className="fw-semibold" style={{ color: '#0f172a' }}>{formatDate(event.start_datetime)}</div>
            <div className="small text-muted">{formatTime(event.start_datetime)}</div>
          </div>

          <div style={{ width: 110 }}>
            <Link to={`/event/${event.id}`} className="btn btn-primary w-100" style={{ borderRadius: 8 }}>
              View
            </Link>
          </div>
        </div>
      </div>
    </article>
  );
}
