import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { login } from "../actions/userActions";
import { Navigate, Link } from "react-router-dom";

export default function LoginScreen() {
  const dispatch = useDispatch();
  const { userInfo, loading, error } = useSelector((s) => s.userLogin);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const submit = (e) => { e.preventDefault(); dispatch(login(username, password)); };
  if (userInfo?.access) return <Navigate to="/" />;

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="logo" style={{ fontSize: '2.5rem', fontWeight: 800, color: '#5561fa', width: '100%', margin: '0 auto 1rem auto' }}>EH</div>
        <h2 style={{ fontWeight: 700, marginBottom: '0.4rem', color: '#22264e' }}>Welcome Back</h2>
        <div style={{ color: '#9595b5', marginBottom: '1.5rem' }}>Sign in to your EventHub account</div>
        {error && <div style={{ color: '#f55', marginBottom: 12, borderRadius: 8, background: '#fff5f5', padding: 10 }}>{String(error)}</div>}
        <form onSubmit={submit} autoComplete="on">
          <input
            type="text"
            placeholder="Enter your username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            autoComplete="username"
            style={{}}
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            autoComplete="current-password"
            style={{}}
          />
          <button type="submit" disabled={loading} style={{ opacity: loading ? 0.6 : 1 }}>
            {loading ? "Signing In..." : "Sign In"}
          </button>
        </form>
        <div className="signup-link">
          Don't have an account?{' '}
          <Link to="/register" style={{ color: '#5561fa', fontWeight: 600, textDecoration: 'none' }}>Sign up here</Link>
        </div>
      </div>
    </div>
  );
}
