import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Hero({ onSearch }) {
  const navigate = useNavigate();
  const slides = [
    {
      url: "https://images.unsplash.com/photo-1500530855697-b586d89ba3ee?q=80&w=1600&auto=format&fit=crop",
      headline: "Discover Amazing Events",
      subtitle: "Find and attend the best events happening around you",
      cta: "Explore Events",
      action: () => navigate("/"),
      gradient: "linear-gradient(135deg, rgba(59, 130, 246, 0.85), rgba(30, 64, 175, 0.85))",
      icon: "ðŸŽ‰"
    },
    {
      url: "https://images.unsplash.com/photo-1515165562835-c3b8c20b2e68?q=80&w=1600&auto=format&fit=crop",
      headline: "Create Your Event",
      subtitle: "Organize and share your events with the world",
      cta: "Start Creating",
      action: () => navigate("/organizer/create"),
      gradient: "linear-gradient(135deg, rgba(16, 185, 129, 0.85), rgba(5, 150, 105, 0.85))",
      icon: "âœ¨"
    },
    {
      url: "https://images.unsplash.com/photo-1492684223066-81342ee5ff30?q=80&w=1600&auto=format&fit=crop",
      headline: "Connect & Network",
      subtitle: "Meet like-minded people and build lasting connections",
      cta: "Join Now",
      action: () => navigate("/register"),
      gradient: "linear-gradient(135deg, rgba(139, 92, 246, 0.85), rgba(124, 58, 237, 0.85))",
      icon: "ðŸ¤"
    },
  ];


  const [idx, setIdx] = useState(0);
  const [isLoaded, setIsLoaded] = useState(false);
  const next = () => setIdx((i) => (i + 1) % slides.length);
  const prev = () => setIdx((i) => (i - 1 + slides.length) % slides.length);

  useEffect(() => {
    const id = setInterval(next, 6000);
    return () => clearInterval(id);
  }, []);

  useEffect(() => {
    setIsLoaded(true);
  }, []);


  return (
    <>
      <section className={`hero-carousel position-relative ${isLoaded ? 'hero-loaded' : ''}`}>
        <div className="hero-background">
          <img src={slides[idx].url} alt="hero" className="w-100 hero-slide" />
          <div className="hero-background-overlay"></div>
        </div>
        <div
          className="hero-overlay"
          style={{ background: slides[idx].gradient }}
        >
          <div className="container-fluid px-4">
            <div className="row justify-content-center text-center">
              <div className="col-lg-10 col-xl-8">
                <div className="hero-content">
                  <div className="hero-icon mb-3">
                    <span style={{ fontSize: '3rem' }}>{slides[idx].icon}</span>
                  </div>
                  <h1 className="display-2 fw-bold mb-4 hero-headline">
                    {slides[idx].headline}
                  </h1>
                  <p className="lead mb-5 fs-4 hero-subtitle">
                    {slides[idx].subtitle}
                  </p>
                  <div className="hero-actions">
                    <button className="btn btn-light btn-xl px-5 py-3 me-3 hero-cta" onClick={slides[idx].action}>
                      <i className="bi bi-arrow-right-circle me-2"></i>
                      {slides[idx].cta}
                    </button>
                    <button className="btn btn-outline-light btn-xl px-4 py-3 hero-secondary" onClick={() => window.scrollTo({ top: document.body.scrollHeight / 3, behavior: "smooth" })}>
                      Browse Categories
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Enhanced Navigation */}
        <button className="hero-nav hero-prev" onClick={prev} aria-label="Previous slide">
          <i className="bi bi-chevron-left"></i>
        </button>
        <button className="hero-nav hero-next" onClick={next} aria-label="Next slide">
          <i className="bi bi-chevron-right"></i>
        </button>

        {/* Enhanced Slide Indicators */}
        <div className="hero-indicators">
          <div className="d-flex gap-3">
            {slides.map((_, i) => (
              <button
                key={i}
                className={`hero-indicator ${i === idx ? 'active' : ''}`}
                onClick={() => setIdx(i)}
                aria-label={`Go to slide ${i + 1}`}
              >
                <div className="indicator-dot"></div>
              </button>
            ))}
          </div>
        </div>

      </section>
    </>
  );
}
