import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { login } from "../actions/userActions";
import { Navigate, Link } from "react-router-dom";

export default function LoginScreen() {
  const dispatch = useDispatch();
  const { userInfo, loading, error } = useSelector((s) => s.userLogin);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const submit = (e) => { e.preventDefault(); dispatch(login(username, password)); };
  if (userInfo?.access) return <Navigate to="/" />;

  return (
    <div className="login-bg-wrapper d-flex flex-column justify-content-center align-items-center" style={{ width: '100vw', minHeight: '82vh', background: 'linear-gradient(135deg, #e0ecfa 0%, #b6c3f7 100%)', position: 'relative', zIndex: 1 }}>
      <div style={{
        width: '100%', maxWidth: 610, minWidth: 350, padding: '0 12px', minHeight: 540,
        display: 'flex', justifyContent: 'center', alignItems: 'center',
        background: 'linear-gradient(135deg, #dde8fd 60%, #c7d2fe 100%)', borderRadius: 32, boxShadow: '0 16px 48px 8px rgba(102,126,234,0.09)',
      }}>
        <div className="card border-0 shadow-lg w-100" style={{ maxWidth: 470, margin: '0 auto', borderRadius: '20px', backdropFilter: 'blur(8px)', background: 'rgba(255,255,255,0.98)' }}>
          <div className="card-body px-5 py-5">
            <div style={{ textAlign: 'center' }} className="mb-3">
              <div className="logo mb-1" style={{ fontSize: '2.5rem', fontWeight: 900, color: '#4f65d6', letterSpacing: 0 }}>EH</div>
              <h2 className="fw-bold mb-2" style={{ fontSize: '2.1rem', color: '#374151', letterSpacing: '-0.02em', lineHeight: '1.2' }}>Welcome Back</h2>
              <p className="text-muted mb-4" style={{ fontSize: '1.05rem', whiteSpace:'normal' }}>Sign in to your EventHub account</p>
            </div>
            {error && <div className="alert alert-danger" style={{ borderRadius: '10px' }}>{String(error)}</div>}

            <form onSubmit={submit} autoComplete="on">
              <div className="mb-4">
                <label className="form-label fw-semibold" htmlFor="login-username">Username</label>
                <div className="input-group">
                  <span className="input-group-text bg-white" style={{ borderRadius: '10px 0 0 10px', borderRight: 0 }}>
                    <i className="bi bi-person" style={{ fontSize: '1.1rem' }}></i>
                  </span>
                  <input
                    id="login-username"
                    className="form-control"
                    style={{ borderRadius: '0 10px 10px 0', borderLeft: 0, paddingLeft: 0, fontSize: '1rem', height: 46 }}
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                    placeholder="Enter your username"
                    autoFocus
                    autoComplete="username"
                  />
                </div>
              </div>
              <div className="mb-4">
                <label className="form-label fw-semibold" htmlFor="login-password">Password</label>
                <div className="input-group">
                  <span className="input-group-text bg-white" style={{ borderRadius: '10px 0 0 10px', borderRight: 0 }}>
                    <i className="bi bi-lock" style={{ fontSize: '1.1rem' }}></i>
                  </span>
                  <input
                    id="login-password"
                    type="password"
                    className="form-control"
                    style={{ borderRadius: '0 10px 10px 0', borderLeft: 0, paddingLeft: 0, fontSize: '1rem', height: 46 }}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    placeholder="Enter your password"
                    autoComplete="current-password"
                  />
                </div>
              </div>

              <button
                className="btn btn-primary w-100 py-3 mt-2 mb-2"
                type="submit"
                disabled={loading}
                style={{ borderRadius: '10px', fontWeight: 600, fontSize: '1.17rem', background: 'linear-gradient(135deg, #667eea 0%, #4f65d6 80%)', border: 'none', minHeight: 52 }}
              >
                {loading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Signing In...
                  </>
                ) : (
                  'Sign In'
                )}
              </button>
            </form>
            <div className="text-center mt-4">
              <p className="mb-0 text-muted">Don't have an account? <Link to="/register" className="text-decoration-none fw-semibold" style={{ color: '#4f65d6' }}>Sign up here</Link></p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
