import React from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import HomeScreen from "./screens/HomeScreen";
import EventDetailScreen from "./screens/EventDetailScreen";
import CreateEventScreen from "./screens/CreateEventScreen";
import MyBookingsScreen from "./screens/MyBookingsScreen";
import LoginScreen from "./screens/LoginScreen";
import RegisterScreen from "./screens/RegisterScreen";
import ProfileScreen from "./screens/ProfileScreen";
import Header from "./components/Header";
import Footer from "./components/Footer";
import ProtectedRoute from "./components/ProtectedRoute";

export default function App() {
  return (
    <div className="d-flex flex-column min-vh-100">
      <Header />
      <main className="container my-4 flex-fill">
        <Routes>
          <Route path="/" element={<HomeScreen />} />
          <Route path="/event/:id" element={<EventDetailScreen />} />
          <Route path="/login" element={<LoginScreen />} />
          <Route path="/register" element={<RegisterScreen />} />
          <Route path="/profile" element={<ProtectedRoute roles={["attendee", "organizer"]}><ProfileScreen /></ProtectedRoute>} />
          <Route path="/bookings" element={<ProtectedRoute roles={["attendee", "organizer"]}><MyBookingsScreen /></ProtectedRoute>} />
          <Route path="/organizer/create" element={<ProtectedRoute roles={["organizer"]}><CreateEventScreen /></ProtectedRoute>} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </main>
      <Footer />
    </div>
  );
}
