import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Header() {
  const navigate = useNavigate();
  const userInfo = localStorage.getItem("userInfo") ? JSON.parse(localStorage.getItem("userInfo")) : null;
  const logout = () => {
    localStorage.removeItem("userInfo");
    navigate("/login");
  };

  return (
    <nav className="navbar navbar-expand-lg bg-body-tertiary shadow-sm sticky-top">
      <div className="container">
        <Link className="navbar-brand fw-bold" to="/">EventHub</Link>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#mainNav"><span className="navbar-toggler-icon" /></button>
        <div className="collapse navbar-collapse" id="mainNav">
          <ul className="navbar-nav ms-auto align-items-lg-center gap-lg-2">
            <li className="nav-item"><Link className="nav-link" to="/">Home</Link></li>
            {userInfo ? (
              <>
                <li className="nav-item"><Link className="nav-link" to="/bookings">My Tickets</Link></li>
                {userInfo.role === "organizer" && (
                  <>
                    <li className="nav-item"><Link className="nav-link" to="/organizer/events">My Events</Link></li>
                    <li className="nav-item"><Link className="nav-link" to="/organizer/create">Create Event</Link></li>
                    <li className="nav-item"><Link className="nav-link" to="/organizer/verify">Verify</Link></li>
                  </>
                )}
                <li className="nav-item"><Link className="btn btn-outline-primary" to="/profile">{userInfo.username || "Profile"}</Link></li>
                <li className="nav-item"><button className="btn btn-link nav-link" onClick={logout}>Logout</button></li>
              </>
            ) : (
              <>
                <li className="nav-item"><Link className="btn btn-outline-primary" to="/login">Login</Link></li>
                <li className="nav-item"><Link className="btn btn-primary ms-lg-2" to="/register">Register</Link></li>
              </>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
}
