import React, { useState } from "react";

export default function EventFilter({ onChange }) {
  const [query, setQuery] = useState({ search: "", location: "", category: "", start_after: "", start_before: "" });
  const update = (e) => {
    const next = { ...query, [e.target.name]: e.target.value };
    setQuery(next);
    onChange(next);
  };
  return (
    <div className="filter-section">
      <div className="row g-3 align-items-center">
        <div className="col-lg-3 col-md-6">
          <input name="search" className="form-control form-control-lg" placeholder="Search events, tags or organizers" value={query.search} onChange={update} />
        </div>
        <div className="col-lg-3 col-md-6">
          <input name="location" className="form-control form-control-lg" placeholder="Location" value={query.location} onChange={update} />
        </div>
        <div className="col-lg-2 col-md-6">
          <input name="category" className="form-control form-control-lg" placeholder="Category" value={query.category} onChange={update} />
        </div>
        <div className="col-lg-2 col-md-3">
          <input name="start_after" type="date" className="form-control form-control-lg" value={query.start_after} onChange={update} />
        </div>
        <div className="col-lg-2 col-md-3">
          <input name="start_before" type="date" className="form-control form-control-lg" value={query.start_before} onChange={update} />
        </div>
      </div>
    </div>
  );
}
