import React, { useEffect, useState } from "react";

export default function Hero() {
  const slides = [
    {
      url: "https://images.unsplash.com/photo-1500530855697-b586d89ba3ee?q=80&w=1600&auto=format&fit=crop",
      headline: "Make your next moment",
      cta: "Get Tickets Now",
    },
    {
      url: "https://images.unsplash.com/photo-1515165562835-c3b8c20b2e68?q=80&w=1600&auto=format&fit=crop",
      headline: "Discover events near you",
      cta: "Explore",
    },
    {
      url: "https://images.unsplash.com/photo-1492684223066-81342ee5ff30?q=80&w=1600&auto=format&fit=crop",
      headline: "Create an event people love",
      cta: "Start now",
    },
  ];

  const [idx, setIdx] = useState(0);
  const next = () => setIdx((i) => (i + 1) % slides.length);
  const prev = () => setIdx((i) => (i - 1 + slides.length) % slides.length);

  useEffect(() => {
    const id = setInterval(next, 5000);
    return () => clearInterval(id);
  }, []);

  return (
    <>
      <section className="hero-carousel rounded-4 overflow-hidden mb-4 position-relative">
        <img src={slides[idx].url} alt="hero" className="w-100 hero-slide" />
        <div className="hero-overlay p-4 p-md-5">
          <h1 className="display-5 fw-bold mb-3">{slides[idx].headline}</h1>
          <button className="btn btn-light btn-lg shadow-sm">{slides[idx].cta}</button>
        </div>
        <button className="btn btn-light hero-nav hero-prev" onClick={prev} aria-label="Prev">‹</button>
        <button className="btn btn-light hero-nav hero-next" onClick={next} aria-label="Next">›</button>
      </section>

      <div className="categories-scroll d-flex gap-4 pb-3 mb-4">
        {[
          { icon: "balloon", label: "Halloween" },
          { icon: "music-note-beamed", label: "Music" },
          { icon: "emoji-smile", label: "Nightlife" },
          { icon: "masks", label: "Performing & Visual" },
          { icon: "heart", label: "Dating" },
          { icon: "controller", label: "Hobbies" },
          { icon: "easel", label: "Business" },
          { icon: "cup-straw", label: "Food & Drink" },
        ].map((c) => (
          <div key={c.label} className="text-center">
            <div className="category-circle mx-auto">
              <i className={`bi bi-${c.icon}`} />
            </div>
            <div className="category-label mt-2">{c.label}</div>
          </div>
        ))}
      </div>
    </>
  );
}
