import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { listMyTickets } from "../actions/eventActions";
import Loader from "../components/Loader";

export default function MyBookingsScreen() {
  const dispatch = useDispatch();
  const { loading, error, tickets } = useSelector((s) => s.ticketList);

  useEffect(() => { dispatch(listMyTickets()); }, [dispatch]);

  return (
    <div>
      <h3>My Tickets</h3>
      {loading ? <Loader /> : error ? <div className="alert alert-danger">{String(error)}</div> : (
        <div className="row row-cols-1 row-cols-md-3 g-3">
          {tickets.map((t) => (
            <div className="col" key={t.id}>
              <div className="card h-100">
                <div className="card-body">
                  <h5 className="card-title">{t.event.title}</h5>
                  <p className="card-text text-muted">{new Date(t.event.start_datetime).toLocaleString()}</p>
                  <p className="card-text"><strong>Status:</strong> {t.status}</p>
                  {t.qr_image && <img alt="QR" src={t.qr_image} className="img-fluid" />}
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
