import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { listEvents } from "../actions/eventActions";
import Loader from "../components/Loader";
import Hero from "../components/Hero";
import EventCard from "../components/EventCard";
import EventFilter from "../components/EventFilter";

export default function HomeScreen() {
  const dispatch = useDispatch();
  const { loading, error, events } = useSelector((s) => s.eventList);
  const [filters, setFilters] = useState({});

  useEffect(() => {
    dispatch(listEvents(filters));
  }, [dispatch, filters]);

  return (
    <div>
      <Hero onSearch={setFilters} />
      <div className="d-flex align-items-center justify-content-between mb-2">
        <h2 className="m-0">Events in your area</h2>
      </div>
      <ul className="nav nav-underline home-tabs mb-3">
        <li className="nav-item"><button className="nav-link active" onClick={() => setFilters({ ...filters, when: undefined })}>All</button></li>
        <li className="nav-item"><button className="nav-link" onClick={() => setFilters({ ...filters, today: true })}>Today</button></li>
        <li className="nav-item"><button className="nav-link" onClick={() => setFilters({ ...filters, this_weekend: true })}>This weekend</button></li>
      </ul>
      <EventFilter onChange={setFilters} />
      {loading ? <Loader /> : error ? <div className="alert alert-danger">{String(error)}</div> : (
        <div className="row row-cols-1 row-cols-md-3 g-3">
          {events.map((e) => (
            <div className="col" key={e.id}><EventCard event={e} /></div>
          ))}
        </div>
      )}
    </div>
  );
}
