import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { likeEvent } from "../actions/eventActions";

export default function EventCard({ event }) {
  if (!event) return null;

  const [isHovered, setIsHovered] = useState(false);
  const [isLiked, setIsLiked] = useState(false);
  const dispatch = useDispatch();
  const userInfo = useSelector((state) => state.userLogin.userInfo);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    if (diffDays === 0) return "Today";
    if (diffDays === 1) return "Tomorrow";
    if (diffDays < 7) return `${diffDays} days`;
    return date.toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: date.getFullYear() !== now.getFullYear() ? 'numeric' : undefined });
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-US', { hour: 'numeric', minute: '2-digit', hour12: true });
  };

  const getAvailabilityColor = () => {
    const ratio = event.seats_left / event.capacity;
    if (ratio > 0.5) return '#10b981'; // green
    if (ratio > 0.2) return '#f59e0b'; // yellow
    return '#ef4444'; // red
  };

  const getCategoryIcon = (category) => {
    const icons = {
      'Music': 'ðŸŽµ',
      'Nightlife': 'ðŸŒƒ',
      'Performing & Visual': 'ðŸŽ­',
      'Business': 'ðŸ’¼',
      'Food & Drink': 'ðŸ•',
      'Hobbies': 'ðŸŽ®',
      'Dating': 'ðŸ’•',
      'Halloween': 'ðŸŽƒ'
    };
    return icons[category] || 'ðŸŽ«';
  };

  const handleLike = async () => {
    if (!userInfo) return;
    try {
      const result = await dispatch(likeEvent(event.id));
      setIsLiked(result.liked);
    } catch (error) {
      console.error('Like error:', error);
    }
  };

  return (
    <article
      className="event-card animate-fade-in"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      style={{
        borderRadius: 'var(--border-radius)',
        overflow: 'hidden',
        boxShadow: 'var(--card-shadow)',
        transition: 'all 0.3s ease',
        background: 'white',
        height: '100%'
      }}
    >
      <div className="event-card-inner">
        {/* Image Section */}
        <div className="event-image-container" style={{ position: 'relative', height: '180px', overflow: 'hidden' }}>
          {event.poster ? (
            <img
              src={event.poster}
              alt={event.title}
              className="event-image"
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover',
                transition: 'transform 0.3s ease'
              }}
            />
          ) : (
            <div className="event-image-placeholder" style={{
              width: '100%',
              height: '100%',
              background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              color: '#64748b'
            }}>
              <div className="placeholder-icon" style={{ fontSize: '2.2rem', marginBottom: '0.5rem' }}>ðŸŽ«</div>
              <div className="placeholder-pattern"></div>
            </div>
          )}

          {/* Badges */}
          <div className="event-badges" style={{ position: 'absolute', top: '12px', left: '12px', zIndex: 2 }}>
            {event.featured && (
              <div className="badge-featured animate-bounce-in" style={{
                background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
                color: 'white',
                padding: '6px 12px',
                borderRadius: '20px',
                fontSize: '0.75rem',
                fontWeight: '700',
                boxShadow: '0 4px 12px rgba(240, 147, 251, 0.3)',
                display: 'inline-flex',
                alignItems: 'center',
                marginBottom: '8px'
              }}>
                <i className="bi bi-star-fill me-1"></i>
                Featured
              </div>
            )}
            {event.category && (
              <div className="badge-category animate-fade-in" style={{
                background: 'rgba(255, 255, 255, 0.9)',
                color: '#1e293b',
                padding: '4px 8px',
                borderRadius: '12px',
                fontSize: '0.7rem',
                fontWeight: '600',
                backdropFilter: 'blur(10px)',
                display: 'inline-flex',
                alignItems: 'center'
              }}>
                <span className="me-1">{getCategoryIcon(event.category.name)}</span>
                {event.category.name}
              </div>
            )}
          </div>

          {/* Hover Overlay */}
          <div className={`event-overlay ${isHovered ? 'active' : ''}`} style={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0, 0, 0, 0.7)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            opacity: isHovered ? 1 : 0,
            transition: 'opacity 0.3s ease',
            zIndex: 1
          }}>
            <div className="overlay-content">
              <Link to={`/event/${event.id}`} className="btn-view-event btn-animated" style={{
                background: 'var(--primary-gradient)',
                color: 'white',
                border: 'none',
                padding: '0.75rem 1.5rem',
                borderRadius: 'var(--border-radius-sm)',
                fontWeight: '600',
                textDecoration: 'none',
                boxShadow: '0 8px 25px rgba(59, 130, 246, 0.3)',
                transition: 'all 0.3s ease'
              }}>
                <i className="bi bi-eye-fill me-2"></i>
                View Details
              </Link>
            </div>
          </div>
        </div>

        {/* Content Section */}
        <div className="event-content" style={{ padding: '1.5rem' }}>
          <div className="event-header" style={{ marginBottom: '1rem' }}>
            <h3 className="event-title" style={{
              fontSize: '1.25rem',
              fontWeight: '800',
              color: '#1e293b',
              marginBottom: '0.5rem',
              lineHeight: '1.3'
            }}>{event.title}</h3>
            <div className="event-rating" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              <div className="rating-stars" style={{ display: 'flex', gap: '2px' }}>
                {[...Array(5)].map((_, i) => (
                  <i key={i} className={`bi ${i < 4 ? 'bi-star-fill' : 'bi-star'}`} style={{ color: '#fbbf24', fontSize: '0.8rem' }}></i>
                ))}
              </div>
              <span className="rating-text" style={{ fontSize: '0.8rem', color: '#64748b', fontWeight: '600' }}>4.2</span>
            </div>
          </div>

          <p className="event-description" style={{
            color: '#64748b',
            fontSize: '0.9rem',
            lineHeight: '1.5',
            marginBottom: '1rem',
            display: '-webkit-box',
            WebkitLineClamp: 3,
            WebkitBoxOrient: 'vertical',
            overflow: 'hidden'
          }}>{event.description}</p>

          <div className="event-meta" style={{ marginBottom: '1.5rem' }}>
            <div className="meta-item" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '0.5rem', fontSize: '0.85rem', color: '#64748b' }}>
              <i className="bi bi-geo-alt-fill" style={{ color: '#3b82f6' }}></i>
              <span>{event.location}</span>
            </div>
            <div className="meta-item" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', marginBottom: '0.5rem', fontSize: '0.85rem', color: '#64748b' }}>
              <i className="bi bi-calendar-event-fill" style={{ color: '#10b981' }}></i>
              <span>{formatDate(event.start_datetime)}</span>
            </div>
            <div className="meta-item" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', fontSize: '0.85rem', color: '#64748b' }}>
              <i className="bi bi-clock-fill" style={{ color: '#f59e0b' }}></i>
              <span>{formatTime(event.start_datetime)}</span>
            </div>
          </div>

          <div className="event-footer" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div className="availability-info" style={{ flex: 1, marginRight: '1rem' }}>
              <div className="availability-bar" style={{
                width: '100%',
                height: '6px',
                background: 'rgba(0, 0, 0, 0.1)',
                borderRadius: '3px',
                overflow: 'hidden',
                marginBottom: '0.5rem'
              }}>
                <div
                  className="availability-fill"
                  style={{
                    width: `${(event.seats_left / event.capacity) * 100}%`,
                    height: '100%',
                    background: getAvailabilityColor(),
                    transition: 'width 0.3s ease'
                  }}
                ></div>
              </div>
              <div className="availability-text" style={{ fontSize: '0.8rem', color: '#64748b', fontWeight: '600' }}>
                <span className="seats-left">{event.seats_left}</span>
                <span className="seats-total">/{event.capacity}</span>
                <span className="seats-label"> seats left</span>
              </div>
            </div>

            <div className="event-actions" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              {userInfo && (
                <button
                  className={`btn-favorite ${isLiked ? 'liked' : ''} btn-animated`}
                  onClick={handleLike}
                  title={isLiked ? 'Remove from watchlist' : 'Add to watchlist'}
                  style={{
                    border: 'none',
                    background: 'rgba(239, 68, 68, 0.1)',
                    color: isLiked ? '#ef4444' : '#64748b',
                    width: '40px',
                    height: '40px',
                    borderRadius: '50%',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    transition: 'all 0.3s ease',
                    transform: isLiked ? 'scale(1.1)' : 'scale(1)'
                  }}
                >
                  <i className={`bi ${isLiked ? 'bi-heart-fill' : 'bi-heart'}`}></i>
                </button>
              )}
              <Link to={`/event/${event.id}`} className="btn-primary-custom btn-animated" style={{
                background: 'var(--primary-gradient)',
                color: 'white',
                border: 'none',
                padding: '0.5rem 1rem',
                borderRadius: 'var(--border-radius-sm)',
                fontWeight: '600',
                textDecoration: 'none',
                fontSize: '0.85rem',
                boxShadow: '0 4px 12px rgba(59, 130, 246, 0.3)',
                transition: 'all 0.3s ease'
              }}>
                View
              </Link>
            </div>
          </div>
        </div>
      </div>
    </article>
  );
}
