import React from "react";
import { Link } from "react-router-dom";

export default function EventCard({ event }) {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = date - now;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 0) return "Today";
    if (diffDays === 1) return "Tomorrow";
    if (diffDays < 7) return `${diffDays} days`;

    return date.toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric',
      year: date.getFullYear() !== now.getFullYear() ? 'numeric' : undefined
    });
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-US', { 
      hour: 'numeric', 
      minute: '2-digit',
      hour12: true 
    });
  };

  return (
    <div className="event-card">
      {event.poster && (
        <div className="position-relative">
          <img src={event.poster} className="card-img-top" alt={event.title} />
          {event.featured && (
            <div className="position-absolute top-0 end-0 m-3">
              <span className="badge" style={{
                background: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
                color: 'white',
                padding: '0.5rem 1rem',
                borderRadius: '20px',
                fontSize: '0.8rem',
                fontWeight: '600'
              }}>
                ‚≠ê Featured
              </span>
            </div>
          )}
        </div>
      )}
      <div className="card-body d-flex flex-column">
        <div className="d-flex justify-content-between align-items-start mb-3">
          <h5 className="card-title">{event.title}</h5>
        </div>
        <p className="card-text">{event.description}</p>
        <div className="location mb-3">
          <i className="bi bi-geo-alt-fill me-2" style={{ color: '#3b82f6' }} />
          <span>{event.location}</span>
        </div>
        <div className="mt-auto">
          <div className="d-flex justify-content-between align-items-center mb-3">
            <div>
              <div className="date fw-bold">{formatDate(event.start_datetime)}</div>
              <div className="text-muted small">{formatTime(event.start_datetime)}</div>
            </div>
            <div className="text-end">
              {event.price && (
                <div className="fw-bold" style={{ color: '#667eea' }}>
                  ${event.price}
                </div>
              )}
            </div>
          </div>
          <Link 
            to={`/event/${event.id}`} 
            className="btn w-100"
          >
            View Details
          </Link>
        </div>
      </div>
    </div>
  );
}
