import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getEventDetails, rsvpEvent } from "../actions/eventActions";
import Loader from "../components/Loader";

export default function EventDetailScreen() {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { loading, error, event } = useSelector((s) => s.eventDetails);
  const rsvp = useSelector((s) => s.eventRsvp);

  useEffect(() => { dispatch(getEventDetails(id)); }, [dispatch, id]);

  const onRsvp = () => dispatch(rsvpEvent(id));

  if (loading) return <Loader />;
  if (error) return <div className="alert alert-danger">{String(error)}</div>;
  if (!event?.id) return null;
  return (
    <div className="row g-4">
      <div className="col-lg-7">
        <div className="card border-0 shadow-sm">
          {event.poster && <img src={event.poster} alt={event.title} className="card-img-top" />}
          <div className="card-body">
            <h3 className="mb-2">{event.title}</h3>
            <div className="text-muted mb-2">{new Date(event.start_datetime).toLocaleString()} Â· {event.location}</div>
            <p className="mb-0">{event.description}</p>
          </div>
        </div>
      </div>
      <div className="col-lg-5">
        <div className="card border-0 shadow-sm">
          <div className="card-body">
            <div className="d-flex justify-content-between align-items-center mb-2">
              <span className="fw-semibold">Availability</span>
              <span className="badge bg-secondary">{event.seats_left} / {event.capacity}</span>
            </div>
            <button className="btn btn-primary w-100" onClick={onRsvp}>RSVP</button>
            {rsvp.ticket && (
              <div className="mt-3 text-center">
                <div className="fw-semibold mb-1">Ticket ID</div>
                <div className="small text-muted mb-2">{rsvp.ticket.id}</div>
                {rsvp.ticket.qr_image && <img alt="Ticket QR" src={rsvp.ticket.qr_image} className="img-thumbnail" />}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
