import React, { useState } from "react";

export default function EventFilter({ onChange }) {
  const [query, setQuery] = useState({ search: "", location: "", category: "", start_after: "" });

  const categories = [
    "",
    "Music",
    "Nightlife",
    "Performing & Visual",
    "Business",
    "Food & Drink",
    "Hobbies",
    "Dating",
    "Halloween",
  ];

  const update = (e) => {
    const next = { ...query, [e.target.name]: e.target.value };
    setQuery(next);
    onChange(next);
  };

  const clearAll = () => {
    const reset = { search: "", location: "", category: "", start_after: "" };
    setQuery(reset);
    onChange(reset);
  };

  return (
    <div className="row g-3 align-items-end">
      <div className="col-lg-3 col-md-6">
        <div className="input-group filter-input">
          <span className="input-group-text input-icon"><i className="bi bi-search" /></span>
          <input name="search" className="form-control" placeholder="Search events" value={query.search} onChange={update} />
        </div>
      </div>
      <div className="col-lg-3 col-md-6">
        <div className="input-group filter-input">
          <span className="input-group-text input-icon"><i className="bi bi-geo-alt" /></span>
          <input name="location" className="form-control" placeholder="Location" value={query.location} onChange={update} />
        </div>
      </div>
      <div className="col-lg-2 col-md-6">
        <div className="input-group filter-input">
          <span className="input-group-text input-icon"><i className="bi bi-tag" /></span>
          <select name="category" className="form-select" value={query.category} onChange={update}>
            {categories.map((c) => (
              <option key={c} value={c}>{c === "" ? "All categories" : c}</option>
            ))}
          </select>
        </div>
      </div>
      <div className="col-lg-3 col-md-6">
        <div className="input-group filter-input">
          <span className="input-group-text input-icon"><i className="bi bi-calendar-date" /></span>
          <input name="start_after" type="date" className="form-control" value={query.start_after} onChange={update} />
        </div>
      </div>
      <div className="col-12 d-flex justify-content-end">
        <button type="button" className="btn btn-light px-4" onClick={clearAll}>Clear filters</button>
      </div>
    </div>
  );
}
