import React, { useState, useRef } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { createEvent } from "../actions/eventActions";

export default function CreateEventScreen() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading, error, success } = useSelector((s) => s.eventCreate || {});

  const [form, setForm] = useState({ title: "", description: "", start_datetime: "", location: "", capacity: 0, featured: false });
  const [poster, setPoster] = useState(null);
  const [images, setImages] = useState([]);
  const posterRef = useRef(null);
  const imagesRef = useRef(null);

  const change = (e) => setForm({ ...form, [e.target.name]: e.target.type === "checkbox" ? e.target.checked : e.target.value });
  const submit = (e) => {
    e.preventDefault();
    const fd = new FormData();
    Object.entries(form).forEach(([k, v]) => fd.append(k, v));
    if (poster) fd.append("poster", poster);
    if (images && images.length) {
      Array.from(images).forEach((file) => fd.append("images", file));
    }
    dispatch(createEvent(fd));
  };

  const resetAll = () => {
    setForm({ title: "", description: "", start_datetime: "", location: "", capacity: 0, featured: false });
    setPoster(null);
    setImages([]);
    if (posterRef.current) posterRef.current.value = "";
    if (imagesRef.current) imagesRef.current.value = "";
  };

  return (
    <div className="row justify-content-center">
      <div className="col-lg-8">
        <div className="card border-0 shadow-sm">
          <div className="card-body">
            <div className="d-flex justify-content-between align-items-center mb-3">
              <h3 className="mb-0">Create Event</h3>
              <button className="btn btn-outline-secondary" onClick={() => navigate(-1)}>Back</button>
            </div>
            {error && <div className="alert alert-danger">{String(error)}</div>}
            {success && <div className="alert alert-success">Event created!</div>}
            <form onSubmit={submit} onReset={resetAll}>
              <div className="row g-3">
                <div className="col-md-8"><label className="form-label">Title</label><input name="title" className="form-control" value={form.title} onChange={change} required /></div>
                <div className="col-md-4"><label className="form-label">Capacity</label><input name="capacity" type="number" min="0" className="form-control" value={form.capacity} onChange={change} required /></div>
                <div className="col-12"><label className="form-label">Description</label><textarea name="description" className="form-control" rows="4" value={form.description} onChange={change} required /></div>
                <div className="col-md-6"><label className="form-label">Start Date/Time</label><input name="start_datetime" type="datetime-local" className="form-control" value={form.start_datetime} onChange={change} required /></div>
                <div className="col-md-6"><label className="form-label">Location</label><input name="location" className="form-control" value={form.location} onChange={change} required /></div>
                <div className="col-md-6"><label className="form-label">Poster</label><input ref={posterRef} type="file" accept="image/*" className="form-control" onChange={(e) => setPoster(e.target.files[0])} /></div>
                <div className="col-md-6 d-flex align-items-end"><div className="form-check"><input id="featured" name="featured" type="checkbox" className="form-check-input" checked={form.featured} onChange={change} /><label htmlFor="featured" className="form-check-label ms-2">Featured</label></div></div>
                <div className="col-12">
                  <label className="form-label">Gallery Images</label>
                  <input
                    ref={imagesRef}
                    type="file"
                    accept="image/*"
                    multiple
                    className="form-control"
                    onChange={(e) => setImages(e.target.files)}
                  />
                  {images && images.length > 0 && (
                    <div className="mt-2 d-flex flex-wrap gap-2">
                      {Array.from(images).map((file, idx) => (
                        <div key={idx} className="border rounded overflow-hidden" style={{ width: 80, height: 80 }}>
                          <img
                            src={URL.createObjectURL(file)}
                            alt={file.name}
                            style={{ width: "100%", height: "100%", objectFit: "cover" }}
                          />
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
              <div className="mt-3 d-flex gap-2">
                <button className="btn btn-primary" type="submit" disabled={loading}>Create</button>
                <button className="btn btn-outline-secondary" type="reset">Reset</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
