import React, { useState } from "react";

export default function EventFilter({ onChange }) {
  const [query, setQuery] = useState({ search: "", location: "", category: "", start_after: "", start_before: "" });
  const update = (e) => {
    const next = { ...query, [e.target.name]: e.target.value };
    setQuery(next);
    onChange(next);
  };
  return (
    <div className="row g-2 mb-3">
      <div className="col-md-3"><input name="search" className="form-control" placeholder="Search" value={query.search} onChange={update} /></div>
      <div className="col-md-3"><input name="location" className="form-control" placeholder="Location" value={query.location} onChange={update} /></div>
      <div className="col-md-2"><input name="category" className="form-control" placeholder="Category" value={query.category} onChange={update} /></div>
      <div className="col-md-2"><input name="start_after" type="date" className="form-control" value={query.start_after} onChange={update} /></div>
      <div className="col-md-2"><input name="start_before" type="date" className="form-control" value={query.start_before} onChange={update} /></div>
    </div>
  );
}
