import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { register } from "../actions/userActions";
import { Navigate } from "react-router-dom";

export default function RegisterScreen() {
  const dispatch = useDispatch();
  const { userInfo, loading, error } = useSelector((s) => s.userRegister);
  const [form, setForm] = useState({ username: "", email: "", password: "", first_name: "", last_name: "", role: "attendee" });
  const change = (e) => setForm({ ...form, [e.target.name]: e.target.value });
  const submit = (e) => { e.preventDefault(); dispatch(register(form)); };
  if (userInfo?.access) return <Navigate to="/" />;

  return (
    <div className="row justify-content-center">
      <div className="col-md-6">
        <h3>Register</h3>
        {error && <div className="alert alert-danger">{String(error)}</div>}
        <form onSubmit={submit}>
          <div className="row g-2">
            <div className="col-md-6 mb-3"><label className="form-label">Username</label><input name="username" className="form-control" value={form.username} onChange={change} required /></div>
            <div className="col-md-6 mb-3"><label className="form-label">Email</label><input name="email" type="email" className="form-control" value={form.email} onChange={change} required /></div>
            <div className="col-md-6 mb-3"><label className="form-label">First Name</label><input name="first_name" className="form-control" value={form.first_name} onChange={change} /></div>
            <div className="col-md-6 mb-3"><label className="form-label">Last Name</label><input name="last_name" className="form-control" value={form.last_name} onChange={change} /></div>
            <div className="col-md-6 mb-3"><label className="form-label">Password</label><input name="password" type="password" className="form-control" value={form.password} onChange={change} required /></div>
            <div className="col-md-6 mb-3"><label className="form-label">Role</label>
              <select name="role" className="form-select" value={form.role} onChange={change}>
                <option value="attendee">Attendee</option>
                <option value="organizer">Organizer</option>
              </select>
            </div>
          </div>
          <button className="btn btn-primary" type="submit" disabled={loading}>Create Account</button>
        </form>
      </div>
    </div>
  );
}
