import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { listEvents } from "../actions/eventActions";
import Loader from "../components/Loader";
import Hero from "../components/Hero";
import EventCard from "../components/EventCard";
import EventFilter from "../components/EventFilter";

export default function HomeScreen() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { loading, error, events } = useSelector((s) => s.eventList);
  const [filters, setFilters] = useState({});
  const [activeTab, setActiveTab] = useState('all');

  useEffect(() => {
    dispatch(listEvents(filters));
  }, [dispatch, filters]);

  const handleTabChange = (tab, filterValue) => {
    setActiveTab(tab);
    setFilters({ ...filters, when: filterValue });
  };

  return (
    <div className="home-screen">
      <Hero onSearch={setFilters} />

      {/* Enhanced Section Header */}
      <div className="container-fluid px-4 mt-5">
        <div className="row justify-content-center">
          <div className="col-12">
            <h2 className="section-header">üéâ Events in Your Area</h2>
          </div>
        </div>
      </div>

      {/* Enhanced Filter Tabs */}
      <div className="container-fluid px-4">
        <div className="row justify-content-center">
          <div className="col-lg-8">
            <ul className="nav nav-pills home-tabs justify-content-center">
              <li className="nav-item">
                <button 
                  className={`nav-link ${activeTab === 'all' ? 'active' : ''}`}
                  onClick={() => handleTabChange('all', undefined)}
                >
                  üîç All Events
                </button>
              </li>
              <li className="nav-item">
                <button 
                  className={`nav-link ${activeTab === 'today' ? 'active' : ''}`}
                  onClick={() => handleTabChange('today', true)}
                >
                  üìÖ Today
                </button>
              </li>
              <li className="nav-item">
                <button 
                  className={`nav-link ${activeTab === 'weekend' ? 'active' : ''}`}
                  onClick={() => handleTabChange('weekend', true)}
                >
                  üéä This Weekend
                </button>
              </li>
            </ul>
          </div>
        </div>
      </div>

      {/* Enhanced Filter Section */}
      <div className="container-fluid px-4">
        <div className="row justify-content-center">
          <div className="col-lg-10">
            <div className="filter-section">
              <EventFilter onChange={setFilters} />
            </div>
          </div>
        </div>
      </div>

      {/* Enhanced Events Grid */}
      <div className="container-fluid px-4">
        <div className="row justify-content-center">
          <div className="col-12">
            {loading ? (
              <div className="d-flex justify-content-center py-5">
                <Loader />
              </div>
            ) : error ? (
              <div className="row justify-content-center">
                <div className="col-lg-6">
                  <div className="alert alert-danger text-center" style={{ 
                    borderRadius: 'var(--border-radius)',
                    boxShadow: 'var(--card-shadow)'
                  }}>
                    <i className="bi bi-exclamation-triangle-fill me-2"></i>
                    {String(error)}
                  </div>
                </div>
              </div>
            ) : events.length === 0 ? (
              <div className="row justify-content-center">
                <div className="col-lg-6 text-center py-5">
                  <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>üîç</div>
                  <h3 style={{ color: '#718096', marginBottom: '1rem' }}>No Events Found</h3>
                  <p style={{ color: '#a0aec0' }}>Try adjusting your filters or check back later for new events!</p>
                </div>
              </div>
            ) : (
              <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 row-cols-xl-4 g-4">
                {events.map((event) => (
                  <div className="col" key={event.id}>
                    <EventCard event={event} />
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Call to Action Section */}
      {!loading && events.length > 0 && (
        <div className="container-fluid px-4 mt-5 mb-5">
          <div className="row justify-content-center">
            <div className="col-lg-8 text-center">
              <div style={{
                background: 'var(--primary-gradient)',
                borderRadius: 'var(--border-radius)',
                padding: '3rem 2rem',
                color: 'white',
                boxShadow: 'var(--card-shadow)'
              }}>
                <h3 className="mb-3">Can't Find What You're Looking For?</h3>
                <p className="mb-4">Create your own event and share it with the community!</p>
                <button className="btn btn-light btn-lg px-4" onClick={() => navigate('/organizer/create')}>
                  üéâ Create Event
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
